mapper.select:
SELECT count(*)
  FROM alarm_history ah
 WHERE 1=1
   AND ah.event_type = #{event_type}
   AND ah.resource_id = #{resource_id}
   AND ah.resource_type = #{resource_type}
   AND ah.occure_date = date(#{occure_time})
   AND ah.csp_type = #{csp_type}
   AND ah.alarm_impl = #{alarm_impl}

   AND ah.project_cd = #{project_cd}

mapper.insert:
INSERT INTO alarm_history(event_type, csp_type, resource_id, resource_type, occure_dt, account_id, urgency, plan, note, occure_date, alarm_impl, project_cd)
VALUES (#{event_type}
       , #{csp_type}
       , #{resource_id}
       , #{resource_type}
       , #{occure_time}
       , #{account_id}
       , #{urgency}
       , #{plan}
       , #{note}
       , date(#{occure_time})
       , #{alarm_impl}
       , #{workspace_cd})
    ON DUPLICATE KEY UPDATE occure_dt = #{occure_time}
        , account_id = #{account_id}
        , urgency = #{urgency}
        , plan = #{plan}
        , note = #{note}

mapper.select:
SELECT tcumr.mcmp_mail_receiver AS receiver FROM servicegroup_meta sm JOIN temp_cmp_user_info tcui ON tcui.csp_account_id = sm.csp_account JOIN temp_cmp_user_mail_receiver tcumr ON tcumr.mcmp_user_id = tcui.mcmp_user_id WHERE 1=1
AND sm.csp_instanceid = #{resource_id}
AND sm.service_cd = #{project_cd} AND sm.workspace_cd = #{workspace_cd} group by tcumr.mcmp_mail_receiver
