mapper.select:
/*bill.getCurPrevMonthBill*/ SELECT ( -- 현재월 AWS 데이터 SELECT COALESCE(SUM(ms.total_cost), 0) FROM monthly_summation ms WHERE ms.year_month = #{curYearMonth}
AND ms.csp IN
#{selectedCsp}
AND ms.project_cd IN
#{selectedProject}
AND ms.workspace_cd = #{selectedWorkspace}
) + ( -- 현재월 NCP VM 데이터 (instance_no별 최신 write_date만 반영) SELECT COALESCE(SUM(vm_latest.demand_amount), 0) / 1400.0 FROM ( SELECT n1.instance_no, n1.demand_amount FROM ncp_cost_vm_month n1 INNER JOIN ( SELECT instance_no, demand_month, MAX(write_date) AS latest_write_date FROM ncp_cost_vm_month WHERE demand_month = #{curYearMonth} GROUP BY instance_no, demand_month ) n2 ON n1.instance_no = n2.instance_no AND n1.demand_month = n2.demand_month AND n1.write_date = n2.latest_write_date ) vm_latest
WHERE 1=0
) + ( -- 현재월 Azure VM 데이터 SELECT COALESCE(SUM(acvd.pre_tax_cost), 0) / 1400.0 FROM azure_cost_vm_daily acvd WHERE acvd.usage_date LIKE CONCAT(#{curYearMonth}, '%')
AND 1=0
) AS curMonthBill,
( -- 이전월 AWS 데이터 SELECT COALESCE(SUM(ms.total_cost), 0) FROM monthly_summation ms WHERE ms.year_month = #{prevYearMonth}
AND ms.csp IN
#{selectedCsp}
AND ms.project_cd IN
#{selectedProject}
) + ( -- 이전월 NCP VM 데이터 (instance_no별 최신 write_date만 반영) SELECT COALESCE(SUM(vm_latest.demand_amount), 0) / 1400.0 FROM ( SELECT n1.instance_no, n1.demand_amount FROM ncp_cost_vm_month n1 INNER JOIN ( SELECT instance_no, demand_month, MAX(write_date) AS latest_write_date FROM ncp_cost_vm_month WHERE demand_month = #{prevYearMonth} GROUP BY instance_no, demand_month ) n2 ON n1.instance_no = n2.instance_no AND n1.demand_month = n2.demand_month AND n1.write_date = n2.latest_write_date ) vm_latest
WHERE 1=0
) + ( -- 이전월 Azure VM 데이터 SELECT COALESCE(SUM(acvd.pre_tax_cost), 0) / 1400.0 FROM azure_cost_vm_daily acvd WHERE acvd.usage_date LIKE CONCAT(#{prevYearMonth}, '%')
AND 1=0
) AS prevMonthBill

/*bill.getMonthBill*/ SELECT ym.`year_month`, SUBSTRING(ym.`year_month`, 1, 4) as `year`, SUBSTRING(ym.`year_month`, 5, 2) as `month`, COALESCE(SUM(all_data.total_cost), 0) AS bill FROM (
SELECT #{prevMonth} COLLATE utf8mb4_unicode_ci AS `year_month`
) AS ym LEFT JOIN ( -- AWS 데이터 SELECT ms.`year_month` COLLATE utf8mb4_unicode_ci AS `year_month`, SUM(ms.total_cost) AS total_cost FROM monthly_summation ms WHERE ms.project_cd IN
#{selectedProject}
AND ms.csp IN
#{selectedCsp}
AND ms.`year_month` IN
#{prevMonth}
GROUP BY ms.`year_month`
UNION ALL
-- NCP VM 데이터 (instance_no별 최신 write_date만 반영) SELECT vm_latest.demand_month COLLATE utf8mb4_unicode_ci AS `year_month`, SUM(vm_latest.demand_amount) / 1400.0 AS total_cost FROM ( SELECT n1.instance_no, n1.demand_month, n1.demand_amount FROM ncp_cost_vm_month n1 INNER JOIN ( SELECT instance_no, demand_month, MAX(write_date) AS latest_write_date FROM ncp_cost_vm_month WHERE demand_month IN
#{prevMonth}
GROUP BY instance_no, demand_month ) n2 ON n1.instance_no = n2.instance_no AND n1.demand_month = n2.demand_month AND n1.write_date = n2.latest_write_date ) vm_latest
WHERE 1=0
GROUP BY vm_latest.demand_month
UNION ALL
-- Azure VM 데이터 SELECT DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') COLLATE utf8mb4_unicode_ci AS `year_month`, SUM(acvd.pre_tax_cost) / 1400.0 AS total_cost FROM azure_cost_vm_daily acvd WHERE DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') IN
#{prevMonth}
AND 1=0
GROUP BY DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') ) all_data ON ym.`year_month` = all_data.`year_month` GROUP BY ym.`year_month` ORDER BY ym.`year_month` DESC

SELECT csp, SUM(cost) AS cost FROM (
SELECT 'AWS' AS csp, COALESCE(SUM(co.lineitem_unblendedcost), 0) AS cost FROM tbl_table_billing_detail_${year_month} co LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid WHERE sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate < #{curMonthEndDate}
UNION ALL
SELECT 'NCP' AS csp, COALESCE(SUM(demand_amount), 0) / 1400.0 AS cost FROM ncp_cost_service_month WHERE demand_month = #{year_month}
UNION ALL
SELECT 'AZURE' AS csp, COALESCE(SUM(pre_tax_cost), 0) / 1400.0 AS cost FROM azure_cost_service_daily WHERE DATE_FORMAT(STR_TO_DATE(usage_date, '%Y%m%d'), '%Y%m') = #{year_month} ) AS combined_costs GROUP BY csp
