mapper.select:
/*bill.getCurPrevMonthBill*/ SELECT ( -- 현재월 AWS 데이터 SELECT COALESCE(SUM(ms.total_cost), 0) FROM monthly_summation ms WHERE ms.year_month = #{curYearMonth}
AND ms.csp IN
#{selectedCsp}
AND ms.project_cd IN
#{selectedProject}
) + ( SELECT COALESCE(SUM(nvd_latest.demand_amount), 0) / 1400.0 FROM ncp_cost_vm_month nvd_latest INNER JOIN servicegroup_meta sm ON nvd_latest.instance_no COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'NCP' WHERE nvd_latest.demand_month = #{curYearMonth}
AND sm.service_cd IN
#{selectedProject}
WHERE 1=0
) + ( -- 현재월 Azure VM 데이터 (조인 후 계산) SELECT COALESCE(SUM(acvd.pre_tax_cost), 0) / 1400.0 FROM azure_cost_vm_daily acvd INNER JOIN servicegroup_meta sm ON acvd.vm_id COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'AZURE'
AND sm.service_cd IN
#{selectedProject}
WHERE acvd.usage_date LIKE CONCAT(#{curYearMonth}, '%')
AND 1=0
) AS curMonthBill,
( -- 이전월 AWS 데이터 SELECT COALESCE(SUM(ms.total_cost), 0) FROM monthly_summation ms WHERE ms.year_month = #{prevYearMonth}
AND ms.csp IN
#{selectedCsp}
AND ms.project_cd IN
#{selectedProject}
) + ( -- 이전월 NCP VM 데이터 (조인 후 계산) SELECT COALESCE(SUM(nvd_latest.demand_amount), 0) / 1400.0 FROM ncp_cost_vm_month nvd_latest INNER JOIN servicegroup_meta sm ON nvd_latest.instance_no COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'NCP' WHERE nvd_latest.demand_month = #{prevYearMonth}
AND sm.service_cd IN
#{selectedProject}
AND 1=0
) + ( -- 이전월 Azure VM 데이터 (조인 후 계산) SELECT COALESCE(SUM(acvd.pre_tax_cost), 0) / 1400.0 FROM azure_cost_vm_daily acvd INNER JOIN servicegroup_meta sm ON acvd.vm_id COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'AZURE'
AND sm.service_cd IN
#{selectedProject}
WHERE acvd.usage_date LIKE CONCAT(#{prevYearMonth}, '%')
AND 1=0
) AS prevMonthBill

/*bill.getMonthBill*/ SELECT ym.`year_month` , SUBSTRING(ym.`year_month`, 1, 4) AS `year` , SUBSTRING(ym.`year_month`, 5, 2) AS `month` , COALESCE(SUM(all_data.total_cost), 0) AS bill FROM (
SELECT #{prevMonth} COLLATE utf8mb4_unicode_ci AS `year_month`
) AS ym LEFT JOIN ( -- AWS 데이터 SELECT ms.`year_month` COLLATE utf8mb4_unicode_ci AS `year_month` , SUM(ms.total_cost) AS total_cost FROM monthly_summation ms WHERE ms.project_cd IN
#{selectedProject}
AND ms.csp IN
#{selectedCsp}
AND ms.`year_month` IN
#{prevMonth}
GROUP BY ms.`year_month`
UNION ALL
-- NCP VM 데이터 (servicegroup_meta 조인 후 필터링) SELECT nvd_latest.demand_month COLLATE utf8mb4_unicode_ci AS `year_month`, SUM(nvd_latest.demand_amount) / 1400.0 AS total_cost FROM ncp_cost_vm_month nvd_latest INNER JOIN servicegroup_meta sm ON nvd_latest.instance_no COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'NCP'
AND sm.service_cd IN
#{selectedProject}
AND 1=0
WHERE nvd_latest.demand_month IN
#{prevMonth}
GROUP BY nvd_latest.demand_month
UNION ALL
-- Azure VM 데이터 (servicegroup_meta 조인 후 필터링) SELECT DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') COLLATE utf8mb4_unicode_ci AS `year_month` , SUM(acvd.pre_tax_cost) / 1400.0 AS total_cost FROM azure_cost_vm_daily acvd INNER JOIN servicegroup_meta sm ON acvd.vm_id COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'AZURE'
AND sm.service_cd IN
#{selectedProject}
WHERE DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') IN
#{prevMonth}
AND 1=0
GROUP BY DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') ) AS all_data ON ym.`year_month` = all_data.`year_month` GROUP BY ym.`year_month` ORDER BY ym.`year_month` DESC
