mapper.select:
/*bill.getCurPrevMonthBill*/
SELECT COALESCE(SUM(CASE
                    WHEN co.lineitem_usagestartdate >= #{curMonthStartDate}
                     AND co.lineitem_usageenddate <= #{curMonthEndDate}
                    THEN co.lineitem_unblendedcost
                    ELSE 0
        END), 0) AS curMonthBill
      , COALESCE(SUM(CASE
                     WHEN co.lineitem_usagestartdate >= #{prevMonthStartDate}
                      AND co.lineitem_usageenddate <= #{prevMonthEndDate}
                     THEN co.lineitem_unblendedcost
                     ELSE 0
        END), 0) AS prevMonthBill
  FROM cur_origin co
  LEFT JOIN servicegroup_meta sm
       ON co.lineitem_resourceid = sm.csp_instanceid
 WHERE 1=1

   AND sm.csp_type IN

            #{selectedCsp}


   AND sm.workspace_cd = #{selectedWorkspace}

   AND sm.service_cd IN

             #{selectedProject}



   AND co.lineitem_usagestartdate >= #{prevMonthStartDate}
   AND co.lineitem_usageenddate <= #{curMonthEndDate}

/*bill.getMonthBill*/ SELECT DATE_FORMAT(dates.month_date, '%Y') AS year , DATE_FORMAT(dates.month_date, '%m') AS month , COALESCE(SUM(co.lineitem_unblendedcost), 0) AS bill FROM ( SELECT DATE_FORMAT(DATE_ADD(#{curMonthStartDate}, INTERVAL -(seq - 1) MONTH), '%Y-%m-01') AS month_date FROM ( SELECT 1 AS seq UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5 UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10 UNION SELECT 11 UNION SELECT 12 ) AS seq_table ) AS dates LEFT JOIN cur_origin co ON DATE_FORMAT(co.lineitem_usagestartdate, '%Y%m') = DATE_FORMAT(dates.month_date, '%Y%m') LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND sm.workspace_cd = #{selectedWorkspace} WHERE 1=1 AND dates.month_date BETWEEN DATE_FORMAT(DATE_SUB(#{curMonthStartDate}, INTERVAL 1 YEAR), '%Y-%m-01') AND #{curMonthEndDate} GROUP BY DATE_FORMAT(dates.month_date, '%Y'), DATE_FORMAT(dates.month_date, '%m') ORDER BY year DESC, month DESC LIMIT 12

SELECT productcode AS resourceNm
     , total_cost AS bill
     , sm2.csp_type AS csp
  FROM (
        SELECT CASE WHEN co.lineitem_productcode IN (
                         SELECT lineitem_productcode
                         FROM (
                               SELECT co.lineitem_productcode
                                    , COALESCE(SUM(co.lineitem_unblendedcost), 0) AS total_cost
                                 FROM cur_origin co
                                 LEFT JOIN servicegroup_meta sm
                                   ON co.lineitem_resourceid = sm.csp_instanceid
                                WHERE 1=1

                                  AND sm.csp_type IN

                                    #{selectedCsp}


                                  AND sm.workspace_cd = #{selectedWorkspace}

                                  AND sm.service_cd IN

                                      #{selectedProject}



                                  AND co.lineitem_usagestartdate >= #{curMonthStartDate}
                                  AND co.lineitem_usageenddate <= #{curMonthEndDate}

                                GROUP BY co.lineitem_productcode
                                ORDER BY total_cost DESC
                                LIMIT 5
                         ) AS Top5
                     ) THEN co.lineitem_productcode
                       ELSE 'others'
                  END AS productcode
             , COALESCE(SUM(co.lineitem_unblendedcost), 0) AS total_cost
             , MIN(co.lineitem_resourceid) AS min_resourceid
          FROM cur_origin co
          LEFT JOIN servicegroup_meta sm
            ON co.lineitem_resourceid = sm.csp_instanceid
         WHERE 1=1

           AND sm.csp_type IN

               #{selectedCsp}


           AND sm.workspace_cd = #{selectedWorkspace}

           AND sm.service_cd IN

               #{selectedProject}



           AND co.lineitem_usagestartdate >= #{curMonthStartDate}
           AND co.lineitem_usageenddate <= #{curMonthEndDate}

         GROUP BY productcode
        ) AS grouped_results
  LEFT JOIN servicegroup_meta sm2
    ON grouped_results.min_resourceid = sm2.csp_instanceid
 ORDER BY total_cost DESC;

SELECT COALESCE(SUM(co.lineitem_unblendedcost), 0) AS bill
     , co.lineitem_productcode AS childProductCode
     , count(DISTINCT co.lineitem_resourceid) as unit
  FROM cur_origin co
  LEFT JOIN servicegroup_meta sm
    ON co.lineitem_resourceid = sm.csp_instanceid
 WHERE 1=1

    AND sm.csp_type IN

        #{selectedCsp}


AND sm.workspace_cd = #{selectedWorkspace}

    AND sm.service_cd IN

        #{selectedProject}



AND co.lineitem_usagestartdate >= #{curMonthStartDate}
AND co.lineitem_usageenddate <= #{curMonthEndDate}  


        AND co.lineitem_productcode IN

            #{AWSChildProduct}



        AND sm.service_cd IN ('')


GROUP BY co.lineitem_productcode

/*bill.getCurPrevMonthBill*/
SELECT COALESCE(SUM(CASE
                    WHEN co.lineitem_usagestartdate >= #{curMonthStartDate}
                     AND co.lineitem_usageenddate <= #{curMonthEndDate}
                    THEN co.lineitem_unblendedcost
                    ELSE 0
        END), 0) AS cost
FROM cur_origin co
LEFT JOIN servicegroup_meta sm
ON co.lineitem_resourceid = sm.csp_instanceid
WHERE 1=1

    AND sm.csp_type IN

        #{selectedCsp}


AND sm.workspace_cd = #{selectedWorkspace}

    AND sm.service_cd IN

        #{selectedProject}



   AND co.lineitem_usageenddate <= #{curMonthEndDate}
