mapper.select:
/*bill.getCurPrevMonthBill*/
SELECT (SELECT coalesce(sum(total_cost), 0)
          FROM monthly_summation ms
         WHERE 1=1
          AND ms.`year_month` = #{curYearMonth}

            AND ms.csp IN

                #{selectedCsp}


            AND ms.project_cd IN

                #{selectedProject}

        ) AS curMonthBill
     , (SELECT coalesce(sum(total_cost), 0)
          FROM monthly_summation ms
         WHERE 1=1
          AND ms.`year_month` = #{prevYearMonth}

            AND ms.csp IN

                #{selectedCsp}


            AND ms.project_cd IN

                #{selectedProject}

        ) AS prevMonthBill

/*bill.getMonthBill*/ SELECT COALESCE(SUM(ms.total_cost), 0) AS bill , SUBSTRING(ym.`year_month`, 1, 4) as year , substring(ym.`year_month`, 5, 2) as month FROM (
SELECT #{prevMonth} AS `year_month`
) AS ym LEFT JOIN monthly_summation ms ON ym.`year_month` = ms.`year_month`
AND ms.csp IN
#{selectedCsp}
AND ms.project_cd IN
#{selectedProject}
GROUP BY ym.year_month ORDER BY ym.year_month DESC

SELECT productcode AS resourceNm , total_cost AS bill , sm2.csp_type AS csp FROM ( SELECT CASE WHEN co.lineitem_productcode IN ( SELECT lineitem_productcode FROM ( SELECT co.lineitem_productcode , COALESCE(SUM(co.lineitem_unblendedcost), 0) AS total_cost FROM tbl_table_billing_detail_${year_month} co LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate <= #{curMonthEndDate}
GROUP BY co.lineitem_productcode ORDER BY total_cost DESC LIMIT 5 ) AS Top5 ) THEN co.lineitem_productcode ELSE 'others' END AS productcode , COALESCE(SUM(co.lineitem_unblendedcost), 0) AS total_cost , MIN(co.lineitem_resourceid) AS min_resourceid FROM tbl_table_billing_detail_${year_month} co LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate <= #{curMonthEndDate}
GROUP BY productcode ) AS grouped_results LEFT JOIN servicegroup_meta sm2 ON grouped_results.min_resourceid = sm2.csp_instanceid ORDER BY total_cost DESC

SELECT COALESCE(SUM(co.lineitem_unblendedcost), 0) AS bill , co.lineitem_productcode AS childProductCode , count(DISTINCT co.lineitem_resourceid) as unit FROM tbl_table_billing_detail_${year_month} co LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate <= #{curMonthEndDate}
AND co.lineitem_productcode IN
#{AWSChildProduct}
AND sm.service_cd IN ('')
GROUP BY co.lineitem_productcode

/*bill.getCurPrevMonthBill*/
SELECT COALESCE(SUM(CASE
                    WHEN co.lineitem_usagestartdate >= #{curMonthStartDate}
                     AND co.lineitem_usageenddate <= #{curMonthEndDate}
                    THEN co.lineitem_unblendedcost
                    ELSE 0
        END), 0) AS cost
FROM tbl_table_billing_detail_${year_month} co
LEFT JOIN servicegroup_meta sm
ON co.lineitem_resourceid = sm.csp_instanceid
WHERE 1=1

    AND sm.csp_type IN

        #{selectedCsp}


    AND sm.service_cd IN

        #{selectedProject}


   AND co.lineitem_usageenddate <= #{curMonthEndDate}
