mapper.select:
SELECT COALESCE(SUM(ms.total_cost), 0) AS bill , ym.`year_month` AS yearMonth , csp_providers.csp AS csp FROM (
SELECT #{prevMonth} AS `year_month`
) AS ym CROSS JOIN ( SELECT 'AWS' AS csp UNION ALL SELECT 'GCP' UNION ALL SELECT 'AZURE' UNION ALL SELECT 'NCP' ) AS csp_providers LEFT JOIN monthly_summation ms ON ym.`year_month` = ms.`year_month` AND ms.csp = csp_providers.csp
AND ms.project_cd IN
#{selectedProject}
AND ms.workspace_cd = #{selectedWorkspace} GROUP BY ym.year_month, csp_providers.csp ORDER BY ym.year_month DESC, csp_providers.csp

SELECT co.lineitem_usageaccountid as accountID , co.lineitem_productcode as productID , sm.csp_type as csp , SUM(co.lineitem_unblendedcost) AS bill , co.lineitem_resourceid as resourceID FROM tbl_table_billing_detail_${year_month} co LEFT JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.workspace_cd = #{selectedWorkspace}
AND sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate < #{curMonthEndDate}
AND co.lineitem_unblendedcost >= 0 GROUP BY co.lineitem_usageaccountid, co.lineitem_productcode, sm.csp_type, co.lineitem_resourceid
