mapper.select:
SELECT COALESCE(SUM(ms.total_cost), 0) AS bill , ym.`year_month` AS yearMonth , 'AWS' AS csp FROM (
SELECT #{prevMonth} COLLATE utf8mb4_unicode_ci AS `year_month`
) AS ym LEFT JOIN ( SELECT ms.`year_month` , SUM(ms.total_cost) AS total_cost FROM monthly_summation ms WHERE ms.project_cd IN
#{selectedProject}
AND ms.workspace_cd = #{selectedWorkspace}
AND 1=0
AND ms.`year_month` IN
#{prevMonth}
GROUP BY ms.`year_month` ) ms ON ym.`year_month` = ms.`year_month` GROUP BY ym.`year_month`
UNION ALL
SELECT COALESCE(SUM(cost), 0) AS bill , yearMonth , 'NCP' AS csp FROM (
SELECT COALESCE(SUM(nvd_latest.demand_amount), 0) / 1400.0 AS cost , #{prevMonth} AS yearMonth FROM ( SELECT n1.instance_no , n1.demand_amount FROM ncp_cost_vm_daily n1 INNER JOIN ( SELECT instance_no , MAX(target_date) AS latest_date FROM ncp_cost_vm_daily WHERE DATE_FORMAT(target_date, '%Y%m') = #{prevMonth} GROUP BY instance_no ) n2 ON n1.instance_no = n2.instance_no AND n1.target_date = n2.latest_date ) nvd_latest INNER JOIN servicegroup_meta sm ON nvd_latest.instance_no COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'NCP'
AND sm.workspace_cd = #{selectedWorkspace}
AND sm.service_cd IN
#{selectedProject}
) AS ncp_data WHERE 1=1
AND 1=0
GROUP BY yearMonth
UNION ALL
SELECT COALESCE(SUM(cost), 0) AS bill , yearMonth , 'AZURE' AS csp FROM (
SELECT COALESCE(SUM(acvd.pre_tax_cost), 0) / 1400.0 AS cost , #{prevMonth} AS yearMonth FROM azure_cost_vm_daily acvd INNER JOIN servicegroup_meta sm ON acvd.vm_id COLLATE utf8mb4_unicode_ci = sm.csp_instanceid COLLATE utf8mb4_unicode_ci AND sm.csp_type = 'AZURE'
AND sm.workspace_cd = #{selectedWorkspace}
AND sm.service_cd IN
#{selectedProject}
WHERE DATE_FORMAT(STR_TO_DATE(acvd.usage_date, '%Y%m%d'), '%Y%m') = #{prevMonth}
) AS azure_data WHERE 1=1
AND 1=0
GROUP BY yearMonth ORDER BY yearMonth DESC, csp

SELECT co.lineitem_usageaccountid AS accountID , co.lineitem_productcode AS productID , sm.csp_type AS csp , SUM(co.lineitem_unblendedcost) AS bill , co.lineitem_resourceid AS resourceID FROM tbl_table_billing_detail_${year_month} co INNER JOIN servicegroup_meta sm ON co.lineitem_resourceid = sm.csp_instanceid AND sm.csp_type = 'AWS'
AND sm.workspace_cd = #{selectedWorkspace}
AND sm.service_cd IN
#{selectedProject}
AND co.lineitem_usagestartdate >= #{curMonthStartDate} AND co.lineitem_usageenddate < #{curMonthEndDate}
AND 1=0
GROUP BY co.lineitem_usageaccountid, co.lineitem_productcode, sm.csp_type, co.lineitem_resourceid
