mapper.select:
SELECT ubr.csp_type AS csp , ubr.csp_account AS account , ubr.csp_instanceid AS instance_id , ubr.plan_type AS plan_type , SUM(co.lineitem_unblendedcost) AS saving_cost FROM unused_batch_rst ubr LEFT JOIN servicegroup_meta sm ON ubr.csp_instanceid = sm.csp_instanceid LEFT OUTER JOIN tbl_table_billing_detail_${lastYearMonth} co ON ubr.csp_instanceid = co.lineitem_resourceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND ubr.create_dt = #{curDate} GROUP BY ubr.csp_type, ubr.csp_account, ubr.csp_instanceid, ubr.plan_type

SELECT dabp.product_cd AS product_cd , dabp.abnormal_rating AS abnormal_rating , dabp.percentage_point AS percentage_point , dabp.standard_cost AS standard_cost , dabp.subject_cost AS subject_cost , dabp.csp_type AS csp_type FROM daily_abnormal_by_product dabp WHERE 1=1
AND dabp.csp_type IN
#{selectedCsp}
AND dabp.project_cd IN
#{selectedProject}
AND dabp.collect_dt = #{date}

SELECT iorr.resource_id AS resource_id , iorr.csp_type AS csp_type , iorr.csp_account AS account , iorr.origin_type AS origin_type , iorr.rcmd_type AS rcmd_type , iorr.plan_type AS plan_type , iorr.origin_usd AS origin_usd , iorr.rcmd_usd AS rcmd_usd FROM inst_opti_rcmd_rst iorr LEFT JOIN servicegroup_meta sm ON iorr.resource_id = sm.csp_instanceid WHERE 1=1
AND sm.csp_type IN
#{selectedCsp}
AND sm.service_cd IN
#{selectedProject}
AND iorr.create_dt = #{date}
